package com.mycompany.ClinicaOdontologica.model;import jakarta.persistence.Entity;import lombok.Getter;import lombok.Setter;import java.util.Date;@Getter @Setter@Entitypublic class Dentist extends  Person{    private String especialidad;    public Dentist() {    }    public Dentist(Long idPerson, String dni, String name, String lastname, String phoneNumber, String address, Date birthdate, String especialidad) {        super(idPerson, dni, name, lastname, phoneNumber, address, birthdate);        this.especialidad = especialidad;    }}