package com.mycompany.ClinicaOdontologica.model;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import lombok.Getter;import lombok.Setter;import java.time.LocalDate;@Getter@Setter@Entitypublic class Turn {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE)    private Long idTurn;    private LocalDate dateTurn ;     private String shiftTime;     private String condition ;}