package com.mycompany.ClinicaOdontologica.service;import com.mycompany.ClinicaOdontologica.model.Dentist;import com.mycompany.ClinicaOdontologica.repository.IDentistRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class DentistService implements IDentistService{    @Autowired    private IDentistRepository dentistRepository;    @Override    public void createDentist(Dentist dentist) {        dentistRepository.save(dentist);    }    @Override    public List<Dentist> getDentist() {        List<Dentist> dentistList = dentistRepository.findAll();        return dentistList;    }    @Override    public Dentist findDentist(Long idDentist) {        return dentistRepository.findById(idDentist).orElse(null);    }    @Override    public void deleteDentist(Long idDentist) {        dentistRepository.deleteById(idDentist);    }    @Override    public void editDentist(Dentist dentist) {         this.createDentist(dentist);    }}