package com.company.bazar.controller;import com.company.bazar.model.Client;import com.company.bazar.service.IClientService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseStatus;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/bazar/client")public class ClientController {    @Autowired    private IClientService clientService;    @PostMapping("/create")    @ResponseStatus(HttpStatus.CREATED)    public Client createClient (@RequestBody Client client){        clientService.createCLient(client);        return client;    }    @GetMapping("/get")    @ResponseStatus(HttpStatus.OK)    public List<Client> getClients (){        List<Client>clientList = clientService.getClients();        return clientList;    }    @PutMapping("/edit")    @ResponseStatus(HttpStatus.OK)    public Client editClient (@RequestBody Client client ){        clientService.editClient(client);        return clientService.findClient(client.getIdClient());    }    @DeleteMapping("/delete")    @ResponseStatus(HttpStatus.OK)    public String deleteClient (Long idClient){        clientService.deleteClient(idClient);        return "Client Deleted";    }}