package com.company.bazar.controller;import com.company.bazar.model.Product;import com.company.bazar.service.IProductService;import org.apache.catalina.filters.ExpiresFilter;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseStatus;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/bazar/product")public class ProductController {@Autowiredprivate IProductService productService;    @PostMapping("/create")    @ResponseStatus(HttpStatus.CREATED)    public Product createProduct (@RequestBody Product product){        productService.createProduct(product);        return product;    }    @GetMapping ("/get")    @ResponseStatus(HttpStatus.OK)    public List<Product> getProduct (){        return  productService.getProducts();    }    @DeleteMapping("/delete")    @ResponseStatus(HttpStatus.OK)    public  String deleteProduct (@RequestParam Long codProduct){        productService.deleteProduct(codProduct);        return "Product Deleted";    }    @PutMapping("/edit")    @ResponseStatus(HttpStatus.OK)    public Product editProduct(@RequestBody Product product ){        productService.editProduct(product);        return productService.findProduct(product.getCodProduct());    }}