package com.company.bazar.model;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.JoinColumn;import jakarta.persistence.ManyToOne;import lombok.Getter;import lombok.Setter;@Getter @Setter@Entitypublic class Product {    @Id    @GeneratedValue(strategy =  GenerationType.SEQUENCE)    @Column(name = "cod_product")    private Long codProduct;    private String nameProduct;    private double cost ;    private Long stock;    @ManyToOne    @JoinColumn(name = "cod_sale")    private Sale sale ;}