package com.company.bazar.model;import jakarta.persistence.CascadeType;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.FetchType;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.JoinColumn;import jakarta.persistence.OneToMany;import jakarta.persistence.OneToOne;import lombok.Getter;import lombok.Setter;import java.time.LocalDate;import java.util.Date;import java.util.List;@Getter @Setter@Entitypublic class Sale {    @Id    @GeneratedValue(strategy =  GenerationType.SEQUENCE)    private Long codSale;    private LocalDate saleDate;    private double total ;    @OneToOne    private Client client;    @OneToMany (mappedBy = "sale")    private List<Product> productList ;    public Sale() {    }    public Sale(Long codSale, LocalDate saleDate, double total, Client client, List<Product> productList) {        this.codSale = codSale;        this.saleDate = saleDate;        this.total = total;        this.client = client;        this.productList = productList;    }}