package com.company.bazar.model;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.FetchType;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.JoinColumn;import jakarta.persistence.OneToMany;import jakarta.persistence.OneToOne;import lombok.Getter;import lombok.Setter;import java.util.Date;import java.util.List;@Getter @Setter@Entitypublic class Sale {    @Id    @GeneratedValue(strategy =  GenerationType.SEQUENCE)    @Column(name = "cod_sale")    private Long codSale;    private Date saleDate;    private double total ;    @OneToOne(fetch = FetchType.EAGER)    @JoinColumn(name= "id_client" , referencedColumnName = "id_client")    private Client client;    @OneToMany(mappedBy = "sale",  fetch = FetchType.EAGER )    private List<Product> productList ;    public Sale() {    }    public Sale(Long codSale, Date saleDate, double total, Client client, List<Product> productList) {        this.codSale = codSale;        this.saleDate = saleDate;        this.total = total;        this.client = client;        this.productList = productList;    }}