package com.company.bazar.service;import com.company.bazar.model.Client;import com.company.bazar.repository.IClientRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class CLientService implements IClientService {    @Autowired    IClientRepository clientRepository;    @Override    public void createCLient(Client client) {        clientRepository.save(client);    }    @Override    public List<Client> getClients() {        List<Client> clientList = clientRepository.findAll();        return clientList;    }    @Override    public Client findClient(Long idClient) {        return clientRepository.findById(idClient).orElse(null);    }    @Override    public void deleteClient(Long idClient) {        clientRepository.deleteById(idClient);    }    @Override    public void editClient(Client client) {        this.createCLient(client);    }}