package com.company.bazar.service;import com.company.bazar.model.Product;import com.company.bazar.repository.IProductRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class ProductService implements  IProductService{    @Autowired    IProductRepository productRepository;    @Override    public void createProduct(Product product) {        productRepository.save(product);    }    @Override    public List<Product> getProducts() {        List<Product> productList = productRepository.findAll();        return productList;    }    @Override    public Product findProduct(Long codProduct) {        return productRepository.findById(codProduct).orElse(null);    }    @Override    public void deleteProduct(Long codProduct) {            productRepository.deleteById(codProduct);    }    @Override    public void editProduct(Product product) {        this.createProduct(product);    }}